svg.script:
const svg = document.querySelector('svg');
const status = document.getElementById('status');

svg.addEventListener('click', async (event) => {
  const target = event.target;
  if (!target.closest || !target.closest('rect.button')) {
    return;
  }

  status.textContent = 'Running agent...';
  try {
    const response = await fetch('ai_agent.php', { method: 'POST' });
    const text = await response.text();
    status.textContent = text.trim() || 'Agent completed.';
  } catch (error) {
    status.textContent = 'Agent failed. Check server logs.';
  }
});
